import sys,os,time
string1 = ""
x=sys.getsizeof(string1)
y=1024*1024*1024  #y is in bytes, so, here file size is 1GB
myfile=open('testfile.txt','w')
start=time.clock()
while(int(os.path.getsize('testfile.txt'))<y):
    myfile.write(string1)

myfile.close()
end=time.clock()
print(os.path.getsize('testfile.txt'),"  ........time taken: "+str(end-start)+" seconds")